import React from 'react';
import PropTypes from 'prop-types';
import { ButtonGroup, Card } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import * as movieAPI from '../services/movieAPI';

class MovieDetailsCard extends React.Component {
  deleteMovie(id) {
    // const { id } = this.props.match.params;
    movieAPI.deleteMovie(id);
  }
  render() {
    const { title, storyline, id, imagePath, genre, rating, subtitle} = this.props.movie;

    return (
      <Card data-testid="movie-details">
        <Card.Img variant="top" alt="Movie Cover" src={`../${imagePath}`} />
        <Card.Body>
          <Card.Title>{`Title: ${title}`}</Card.Title>
          <Card.Text>
          <p>{`Subtitle: ${subtitle}`}</p>
          <p>{`Storyline: ${storyline}`}</p>
          <p>{`Genre: ${genre}`}</p>
          <p>{`Rating: ${rating}`}</p>
          </Card.Text>
          <ButtonGroup aria-label="Basic example">
            <Button variant="warning" href={`/movies/${id}/edit`}>EDITAR</Button>
            <Button variant="warning" href="/">VOLTAR</Button>
            <Button variant="warning" href="/" onClick={this.deleteMovie}>DELETAR</Button>
          </ButtonGroup>
        </Card.Body>
      </Card>
    );
  }
}

MovieDetailsCard.propTypes = {
  movie: PropTypes.shape({
    title: PropTypes.string.isRequired,
    storyline: PropTypes.string.isRequired,
    id: PropTypes.number.isRequired,
    imagePath: PropTypes.string.isRequired,
  }).isRequired,
};

export default MovieDetailsCard;
