import React, { Component } from 'react';

import * as movieAPI from '../services/movieAPI';
import { Loading } from '../components';

class MovieDetails extends Component {
  constructor() {
    super();

    this.state = {
      title: '',
      storyline: '',
      imagePath: '',
      subtitle: '',
      genre: '',
      rating: 0,
    };
  }

  componentDidMount() {
    this.request();
  }

  async request() {
    this.setState(
      { loading: true },
      async () => {
        const requestMovie = await movieAPI.getMovie();
        this.setState({
          loading: false,
          title: '',
          storyline: '',
          imagePath: '',
          subtitle: '',
          genre: '',
          rating: 0,
        });
      },
    );
  }
