import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

import * as movieAPI from '../services/movieAPI';
import { Loading } from '../components';


class MovieDetails extends Component {
  constructor() {
    super();

    this.state = {
      movie: {},
      loading: true,
    };

    this.requestMovie = this.requestMovie.bind(this);
    this.deleteMovie = this.deleteMovie.bind(this);
  }

  componentDidMount() {
    this.requestMovie();
  }

  requestMovie() {
    const { id } = this.props.match.params;
    this.setState({ loading: true }, async () => {
      const requestMovie = await movieAPI.getMovie(id);
      this.setState({
        loading: false,
        movie: requestMovie,
      });
    });
  }

  deleteMovie() {
    const { id } = this.props.match.params;
    movieAPI.deleteMovie(id);
  }

  render() {
    const { loading, movie } = this.state;
    const { title, storyline, imagePath, genre, rating, subtitle, id } = movie;

    return (
      loading ? <Loading /> : <div className="details" data-testid="movie-details">
        <img className="image-details" alt="Movie Cover" src={`../${imagePath}`} />
        <div className="text-content-details">
          <p className="text-details" >{`Title: ${title}`}</p>
          <p className="text-details" >{`Subtitle: ${subtitle}`}</p>
          <p className="text-details" >{`Storyline: ${storyline}`}</p>
          <p className="text-details" >{`Genre: ${genre}`}</p>
          <p className="text-details" >{`Rating: ${rating}`}</p>
        </div>
        <nav className="links-content-details">
          <Link to={`/movies/${id}/edit`}>EDITAR</Link>
          <Link to="/">VOLTAR</Link>
          <Link to="/" onClick={this.deleteMovie}>DELETAR</Link>
        </nav>
      </div>
    );
  }
}

MovieDetails.propTypes = {
  match: PropTypes.objectOf(Object).isRequired,
};

export default MovieDetails;
