import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Card } from 'react-bootstrap';
import { Button } from 'react-bootstrap';

class MovieCard extends React.Component {
  render() {
    const { title, storyline, id, imagePath } = this.props.movie;
    return (
      <div className="movie-card" data-testid="movie-card">
        {/* <p className="movie-card-title">{title}</p>
        <p className="movie-card-storyline">{storyline}</p> */}
        {/* <Link to={`/movies/${id}`} className="detalhes-link" >VER DETALHES</Link> */}
      <Card data-testid="movie-card" style={{ width: '18rem' }}>
      <Card.Img variant="top" src={imagePath} />
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <Card.Text>{storyline}</Card.Text>
        <Button href={`/movies/${id}`} variant="primary">VER DETALHES</Button>
      </Card.Body>
    </Card>
      </div>
    );
  }
}

MovieCard.propTypes = {
  movie: PropTypes.shape({
    title: PropTypes.string.isRequired,
    storyline: PropTypes.string.isRequired,
    id: PropTypes.number.isRequired,
  }).isRequired,
};

export default MovieCard;
